import React, { useState, useEffect } from 'react'
import axios from 'axios'
import jwt_decode from "jwt-decode";
import { useNavigate } from 'react-router-dom';


const Dashboard = () => {
    const [name, setName] = useState('');
    const [token, setToken] = useState('');
    const [expire, setExpired] = useState('');
    const [file, setFile] = useState('');
    const [date, setDate] = useState('');
    const [preview, setPreview] = useState('');
    const navigate = useNavigate();

    const loadImage = (e) => {
        const image = e.target.files[0];
        setFile(image);
        setPreview(URL.createObjectURL(image));
    }
    useEffect(() => {
        refreshToken();
    }, []);


    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:5000/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setName(decoded.name);
            setExpired(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate("/");
            }
        }
    }

    const axiosJWT = axios.create();

    axiosJWT.interceptors.request.use(async (config) => {
        const currentDate = new Date();
        if (expire * 1000 < currentDate.getTime()) {
            const response = await axios.get('http://localhost:5000/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setName(decoded.name);
            setExpired(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });


    return (
        <div className="container mt-5">
            <h3 className='title'>Welcome Back, {name}! </h3>
            <div className="columns is-centered mt-5">
                <form action="">
                    <div className="column is-half">
                        <div className='field'>
                            <label className='label'>Nama Karyawan</label>
                            <div className="control">
                                <input type="text" className='input' value={name} disabled />
                            </div>
                        </div>
                        <div className='field'>
                            <label className='label'>Nama Karyawan</label>
                            <div className="control">
                                <input type="text" className='input' value={date} onChange={(e) => setDate(e.target.value)} />
                            </div>
                        </div>
                        <div className='field'>
                            <label className='label'>Nama Karyawan</label>
                            <div className="control">
                                <div className="file">
                                    <label className="file-label">
                                        <input type="file" className='file-input' onChange={loadImage} />
                                        <span className='file-cta'>
                                            <span className='file-label'>Choose a file.</span>
                                        </span>
                                    </label>
                                </div>
                            </div>
                        </div>
                        {preview ? (
                            <figure className='image is-128x128'>
                                <img src={preview} alt="preview image" />
                            </figure>
                        ) : (
                            ""
                        )
                        }
            </div>
                </form>
        </div>
        </div >
    )
}

export default Dashboard
